/**
    encoder func
 */
bool encoder_update_user(uint8_t index, bool clockwise) {
     switch (get_highest_layer(layer_state)) {
         case _MED:
            if (clockwise) {
                tap_code16(KC_UP);
            }else{
                tap_code16(KC_DOWN);
            }
            break;
         case _NAV:
            if (clockwise) {
                tap_code16(KC_RIGHT);
            }else{
                tap_code16(KC_LEFT);
            }
            break;
         case _NUM:
             if (clockwise) {
                 tap_code16(LCTL(KC_RIGHT));
             }else{
                 tap_code16(LCTL(KC_LEFT));
             }
             break;
         case _SPEC:
             oled_write_P(PSTR("Specjalny jeste≈õ \n"), false);
             break;
         default:
            if (clockwise) {
                tap_code16(KC_UP);
                tap_code16(KC_UP);
                tap_code16(KC_UP);
                tap_code16(KC_UP);
                tap_code16(KC_UP);
            }else{
                tap_code16(KC_DOWN);
                tap_code16(KC_DOWN);
                tap_code16(KC_DOWN);
                tap_code16(KC_DOWN);
                tap_code16(KC_DOWN);
            }
     }
  return false;
}

