#ifdef OLED_ENABLE
bool oled_task_user(void) {
    // master side
    if (is_keyboard_master()) {
        // Host Keyboard Layer Status
        oled_write_P(PSTR("Najlepsza klawka \n"), false);
        oled_write_P(PSTR(" \n"), false);
        oled_write_P(PSTR("Warstwa: "), false);

        switch (get_highest_layer(layer_state)) {
            case _COL:
                oled_write_P(PSTR("Colemak_dh \n"), false);
                break;
            case _SYS_NAV:
                oled_write_P(PSTR("Navigacja systemu \n"), false);
                break;
            case _MED:
                oled_write_P(PSTR("Media \n"), false);
                break;
            case _NAV:
                oled_write_P(PSTR("Navigacja \n"), false);
                break;
            case _NUM:
                oled_write_P(PSTR("123451242 \n"), false);
                break;
            case _SPEC:
                oled_write_P(PSTR("Specjalny jeste≈õ \n"), false);
                break;
            default:
                // Or use the write_ln shortcut over adding '\n' to the end of your string
                oled_write_ln_P(PSTR("Teraz to co≈õ popsu≈Çe≈õ"), false);
        }

        // Host Keyboard LED Status
        led_t led_state = host_keyboard_led_state();
        oled_write_P(led_state.num_lock ? PSTR("NUM ") : PSTR("    "), false);
        oled_write_P(led_state.caps_lock ? PSTR("CAP ") : PSTR("    "), false);
        oled_write_P(led_state.scroll_lock ? PSTR("SCR ") : PSTR("    "), false);
        oled_write_P(PSTR("\n"), false);
        oled_write_P(PSTR("Dzban_Diego \n"), false);
        return false;
    // second side
    }else{
        render_logo();
        return false;
    }

}
#endif
